#!/bin/bash

# Script para lanzar Chrome en modo debug y monitorear logs
# Uso: ./test_debug_chrome.sh

set -e

echo "🚀 Lanzando Chrome en modo debug..."

# Matar procesos anteriores de Chrome y Flutter
echo "🧹 Limpiando procesos anteriores..."
pkill -f "chrome.*localhost:8080" 2>/dev/null || true
pkill -f "flutter.*run.*web" 2>/dev/null || true
sleep 2

# Cargar variables de entorno
if [ -f .env ]; then
    export $(cat .env | grep -v '^#' | xargs)
    echo "✅ Variables de entorno cargadas"
else
    echo "⚠️ Archivo .env no encontrado"
fi

# Iniciar Flutter en modo web (background)
echo "🌐 Iniciando servidor Flutter..."
cd "$(dirname "$0")"
flutter run -d chrome-server \
    --dart-define=OPENAI_API_KEY="${OPENAI_API_KEY}" \
    --dart-define=SUPABASE_URL="${SUPABASE_URL}" \
    --dart-define=SUPABASE_ANON_KEY="${SUPABASE_ANON_KEY}" \
    --dart-define=SB_SERVICE_ROLE_KEY="${SB_SERVICE_ROLE_KEY}" \
    --web-port=8080 \
    > /tmp/flutter_web.log 2>&1 &

FLUTTER_PID=$!
echo "📝 Flutter PID: $FLUTTER_PID"
echo "📄 Logs en: /tmp/flutter_web.log"

# Esperar a que el servidor esté listo
echo "⏳ Esperando a que el servidor esté listo..."
for i in {1..30}; do
    if curl -s http://localhost:8080 > /dev/null 2>&1; then
        echo "✅ Servidor activo en http://localhost:8080"
        break
    fi
    if [ $i -eq 30 ]; then
        echo "❌ Timeout esperando servidor"
        kill $FLUTTER_PID 2>/dev/null || true
        exit 1
    fi
    sleep 1
done

# Lanzar Chrome en modo debug con DevTools abierto
echo "🌐 Lanzando Chrome en modo debug..."
/Applications/Google\ Chrome.app/Contents/MacOS/Google\ Chrome \
    --remote-debugging-port=9222 \
    --auto-open-devtools-for-tabs \
    --disable-web-security \
    --user-data-dir=/tmp/chrome-debug-profile \
    http://localhost:8080 \
    > /dev/null 2>&1 &

CHROME_PID=$!
echo "✅ Chrome lanzado (PID: $CHROME_PID)"
echo "🔍 DevTools disponible en: http://localhost:9222"
echo ""
echo "📋 Para monitorear logs:"
echo "   tail -f /tmp/flutter_web.log"
echo ""
echo "🔍 Para acceder a DevTools remoto:"
echo "   http://localhost:9222"
echo ""
echo "⏹️  Para detener:"
echo "   kill $FLUTTER_PID $CHROME_PID"

# Esperar un momento para que Chrome se abra
sleep 3

# Monitorear logs de Flutter en tiempo real
echo ""
